INSERT INTO museum(id, picture_link, name, description, rating, price, city_name, site_link) VALUES(1, 'https://images.unsplash.com/photo-1530110441069-16166bc75658?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1055&q=80', 'Ciudad de las Artes y las Ciencias', 'DISCOVER THE CIUTAT DE LES ARTS I LES CIÈNCIES. Science, nature and art in a complex devoted to scientific and cultural dissemination in Europe, placed in Valencia. The City of Arts and Sciences in Valencia is a unique complex devoted to scientific and cultural dissemination which is made up of five main elements: the Hemisfèric (IMAX cinema and digital projections), the Umbracle (a landscaped vantage point and car park), the Príncipe Felipe Science Museum (an innovative centre of interactive science), the Oceanográfico (the largest aquarium in Europe with over 500 marine species) and the Palau de les Arts Reina Sofía (which takes care of the operatic programme). The Ágora gives the complex a multifunctional space.', 4.5, 65, 'Valencia', 'https://www.cac.es/es/home.html');

INSERT INTO museum(id, picture_link, name, description, rating, price, city_name, site_link) VALUES(2, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/03/e6/e6/f4/museo-de-historia-de.jpg?w=1200&h=-1&s=1', 'Valencia History Museum', 'This museum is located in the old water station that used to supply the city, built in 1850. Made entirely from brick, it represents one of the most attractive examples of 19th-century industrial architecture, with a total 250 columns. This many faceted museum features archaeological, pictorial, documentary, photographic and virtual exhibitions. It deals with the important events that have forged the history of the region, revealing the cultures and customs that have shaped its present-day identity. There is valuable collection of works of art, archaeological objects, scientific instruments, books and costumes. Visitors can immerse themselves in the atmosphere of each era, get to know important historical figures and see what life was like in years gone by.', 4.6, 11.35, 'Valencia', 'https://mhv.valencia.es/');

INSERT INTO museum(id, picture_link, name, description, rating, price, city_name, site_link) VALUES(3, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/0f/a3/92/30/el-museo-vista-desde.jpg?w=1200&h=-1&s=1',
                                                                                                    'Museo de Bellas Artes de Valencia',
                                                                                                    'The Museum of Fine Arts of Valencia is the most complex, rich and varied art institution in the Valencian Community, and is noted first and foremost for its magnificent collection of painting, generally recognized as on of the best in Spain, comprising truly outstanding works of art from all the periods of Valencian painting and other Spanish and international schools, including the great masters of world painting: Velázquez, Ribera, Murillo, Goya, and Sorolla.',
                                                                                                    4, 5, 'Valencia', 'https://museobellasartesvalencia.gva.es/va/'
                                                                                                   );
INSERT INTO museum(id, picture_link, name, description, rating, price, city_name, site_link) VALUES(4, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/11/04/4e/a4/photo0jpg.jpg?w=1200&h=-1&s=1',
                                                                                                    'Museu Faller de València',
                                                                                                    'The Fallero de Valencia Museum, with 121,000 visitors in 2018, has become one of the essential places to visit in the city of Turia. It is a unique and unique museum in the world, since its collections of pardoned ninots, created to be grass of the flames, are chosen by popular vote and saved from the fire to be visited here. Also the Cartel de Fallas collection, signed by important and influential posters and the portraits gallery of Falleras Mayores de Valencia since 1995, surprise people who visit us every day. To fully understand the history of the ninots, we have available audio guides for sale in five languages (Spanish, Valencian, English, French and Italian) at the price of 1 euro. They work using QR code or link to web page on mobile or tablet.',
                                                                                                    4.5,
                                                                                                    7.34,
                                                                                                    'Valencia',
                                                                                                    '"https://www.valencia.es/val/infociutat'
                                                                                                   );
INSERT INTO museum(id, picture_link, name, description, rating, price, city_name, site_link) VALUES(5, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/1b/49/39/b4/facade-laterale.jpg?w=1200&h=-1&s=1',
                                                                                                    'El Museo Nacional de Ceramica y de las Artes Suntuarias Gonzalez Marti',
                                                                                                    'A magnificent building full of some rare artifacts, paintings, ceramics, furniture and carriages! We spent over 2 hours looking at the various rooms full of very interesting things. In most rooms they have provided sheets in various languages explaining the main articles on display in that room. There are even some Picasso plates on display. 3 floors to explore and all rooms were quite different. Enjoyed our visit.',
                                                                                                    4,
                                                                                                    5.25,
                                                                                                    'Valencia',
                                                                                                    'https://www.culturaydeporte.gob.es/mnceramica/home.html'
                                                                                                   );
INSERT INTO museum(id, picture_link, name, description, rating, price, city_name, site_link) VALUES(6, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/07/48/f2/2f/caption.jpg?w=1100&h=-1&s=1',
                                                                                                    'Museu Nacional d''Art de Catalunya - MNAC',
                                                                                                    'An art museum containing hundreds of pieces from the medieval age to the 19th century.',
                                                                                                    4.6,
                                                                                                    14.36,
                                                                                                    'Barcelona',
                                                                                                    'https://www.museunacional.cat/es'
                                                                                                   );
INSERT INTO museum(id, picture_link, name, description, rating, price, city_name, site_link) VALUES(7, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/02/d6/e3/29/parc-de-montjuic.jpg?w=1200&h=-1&s=1',
                                                                                                    'Parc de Montjuic',
                                                                                                    'It''s a mountain that you can access by an aerial lift, or by subway connecting with a funicular railway or just walking. There you''ll find a lot to do, the full of Catalunya history castle, the cemetery, different museums, the Anella Olimpica, huge gardens or the Font Magica. It''s a gorgeous place that deserves the time to fully appreciate its attractions.',
                                                                                                    4.7,
                                                                                                    12.45,
                                                                                                    'Barcelona',
                                                                                                    'http://www.castillomontjuic.com'
                                                                                                   );
INSERT INTO museum(id, picture_link, name, description, rating, price, city_name, site_link) VALUES(8, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/1a/8c/c9/87/photo0jpg.jpg?w=1100&h=-1&s=1',
                                                                                                    'Casa Vicens',
                                                                                                    'Casa Vicens is the first masterpiece of Antoni Gaudi and sowed the seeds of later works. Built between 1883 and 1885 as a summer house for the Vicens family, here he showcased his unparalleled talent. Declared Unesco World Heritage in 2005.',
                                                                                                    4.3,
                                                                                                    19.14,
                                                                                                    'Barcelona',
                                                                                                    'https://casavicens.org'
                                                                                                   );
INSERT INTO museum(id, picture_link, name, description, rating, price, city_name, site_link) VALUES(9, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/13/b9/6e/70/caption.jpg?w=1200&h=-1&s=1',
                                                                                                    'Pergamonmuseum',
                                                                                                    'The Pergamonmuseum is a three-wing complex. The museum houses three of the Staatliche Museen zu Berlin’s collections: the Antikensammlung, Vorderasiatisches Museum, and the Museum für Islamische Kunst. The impressive reconstructions of massive archaeological structures – the Pergamon Altar, Market Gate of Miletus, the Ishtar Gate and Processional Way from Babylon, and the Mshatta Facade – have made the Pergamonmuseum famous throughout the world, with the result that it is the most visited museum at the Staatliche Museen and in Germany as a whole.',
                                                                                                    4.8,
                                                                                                    8.45,
                                                                                                    'Berlin',
                                                                                                    'https://www.smb.museum/en/museums-institutions/pergamonmuseum/home/'
                                                                                                   );

INSERT INTO museum(id, picture_link, name, description, rating, price, city_name, site_link) VALUES(10, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/0b/82/ad/85/img-20160604-wa0095-largejpg.jpg?w=1200&h=-1&s=1',
                                                                                                    'Topography of Terror',
                                                                                                    'Since 1987 a permanent exhibition at the site where the headquarters of the Secret State Police, the SS and the Reich Security Main Office were located during the “Third Reich” has been providing information to the public about the most important institutions of National Socialist persecution and terror. The documentary exhibition conveys the European dimensions of the Nazi reign of terror.',
                                                                                                    4.2,
                                                                                                    6.89,
                                                                                                    'Berlin',
                                                                                                    'https://www.topographie.de/topographie-des-terrors/'
                                                                                                   );
INSERT INTO museum(id, picture_link, name, description, rating, price, city_name, site_link) VALUES(11, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/12/f6/d4/e1/laser-parcours.jpg?w=1000&h=-1&s=1',
                                                                                                    'German Spy Museum Berlin',
                                                                                                    'The German Spy Museum is the only spy museum in Central Europe and charts the history of the second oldest tradecraft in the world in an interactive exhibition for all ages. Situated near Potsdamer Platz the museum is the perfect location to explore the worldwide capital of spies. 2019 refurbished the exhibition is nominated for the European Museum of the Year Award 2020. With more than 400,000 visitors in 2019, the German Spy Museum one of the most visited museums in Berlin Hundreds of unique exhibits like the Nazi cipher machine Enigma, hidden cameras and weapons, bugs or radios are waiting to be explored. Visitors can learn to Morsecode, search bugs, decode messages, try a lie detector, wiggle trough a laser maze or crack a safe. Children can write with secret inks or climb in a dust.',
                                                                                                    4,
                                                                                                    5,
                                                                                                    'Berlin',
                                                                                                    'https://www.deutsches-spionagemuseum.de/'
                                                                                                   );
INSERT INTO museum(id, picture_link, name, description, rating, price, city_name, site_link) VALUES(12, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/07/54/e3/4a/stadel-museum.jpg?w=1200&h=-1&s=1',
                                                                                                    'Stadel Museum',
                                                                                                    'Considered the city''s finest art museum, the Stadel holds a fantastic collection of works from the 14th century through the present day.',
                                                                                                    4.2,
                                                                                                    4.56,
                                                                                                    'Frankfurt',
                                                                                                    'https://www.staedelmuseum.de/en#tp'
                                                                                                   );

INSERT INTO museum(id, picture_link, name, description, rating, price, city_name, site_link) VALUES(13, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/14/a6/35/1e/caption.jpg?w=1200&h=-1&s=1',
                                                                                                    'Senckenberg Natural History Museum',
                                                                                                    'Fantastic display of dinosaurs from tiny trilobites to t-rex. In addition o the main attraction, there is also a floor dedicated to stuffed animals. Visitors of all ages would enjoy this museum.',
                                                                                                    4.5,
                                                                                                    3.56,
                                                                                                    'Frankfurt',
                                                                                                    'https://www.senckenberg.de/de/'
                                                                                                   );
INSERT INTO museum(id, picture_link, name, description, rating, price, city_name, site_link) VALUES(14, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/14/cb/ba/1d/20180925-135248-largejpg.jpg?w=1200&h=-1&s=1',
                                                                                                    'Liebieghaus',
                                                                                                    'Housed in a castle-like structure, this art museum holds an impressive collection of classical, Renaissance and Baroque sculptures. Die Liebieghaus Skulpturensammlung bleibt vom 16. Marz bis einschließlich 10. April geschlossen. Die Liebieghaus Skulpturensammlung folgt damit der behordlichen Vorsorgemaßnahme der Stadt Frankfurt im Zuge der Verbreitung des Coronavirus (Covid-19).',
                                                                                                    4.2,
                                                                                                    5.34,
                                                                                                    'Frankfurt',
                                                                                                    'https://www.liebieghaus.de/de'
                                                                                                   );


INSERT INTO museum(id, picture_link, name, description, rating, price, city_name, site_link) VALUES(15, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/0c/19/a5/7f/caption.jpg?w=1200&h=-1&s=1',
                                                                                                    'Louvre Museum',
                                                                                                    'Home to Leonardo da Vinci''s Mona Lisa, the Louvre is considered the world''s greatest art museum, with an unparalleled collection of items covering the full spectrum of art through the ages.',
                                                                                                    4.8,
                                                                                                    56.24,
                                                                                                    'Paris',
                                                                                                    'https://www.louvre.fr/'
                                                                                                   );
INSERT INTO museum(id, picture_link, name, description, rating, price, city_name, site_link) VALUES(16, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/13/26/b3/6e/20180601-122841-largejpg.jpg?w=1000&h=-1&s=1',
                                                                                                    'Musée d''Orsay',
                                                                                                    'This beautiful museum, once a railroad station, now houses a staggering collection of Impressionist art, as well as other items created between 1848 and 1914. In 2011, the museum is running a renovation of the top floor (impressionist gallery). Only ground and medium floor are accessible. The top floor will re-open on the 20th of October. Meanwhile, some impressionist masterpieces are not visible.',
                                                                                                    4.6,
                                                                                                    28.71,
                                                                                                    'Paris',
                                                                                                    'https://www.musee-orsay.fr/fr'
                                                                                                   );


INSERT INTO museum(id, picture_link, name, description, rating, price, city_name, site_link) VALUES(17, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/0f/ec/32/31/vue-de-la-cour-d-honneur.jpg?w=1200&h=-1&s=1',
                                                                                                    'Musée Rodin',
                                                                                                    'Located in this 18th-century hotel (Hotel Biron), Musee Rodin holds an impressive collection of works by this famous French sculptor and his circle as Camille Claudel. The museum also features an exquisit garden and a temporary exhibition hall.',
                                                                                                    4.3,
                                                                                                    15.65,
                                                                                                    'Paris',
                                                                                                    'https://www.musee-rodin.fr/'
                                                                                                   );

INSERT INTO museum(id, picture_link, name, description, rating, price, city_name, site_link) VALUES(18, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/07/38/60/5e/musee-miniature-et-cinema.jpg?w=1200&h=-1&s=1',
                                                                                                    'Musee du Cinema et de la Miniature',
                                                                                                    'This stately old building is located in Vieux Lyon.',
                                                                                                    4.5,
                                                                                                    11.74,
                                                                                                    'Lyon',
                                                                                                    'https://www.museeminiatureetcinema.fr/'
                                                                                                   );

INSERT INTO museum(id, picture_link, name, description, rating, price, city_name, site_link) VALUES(19, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/08/19/9a/da/musee-des-beaux-arts.jpg?w=1200&h=-1&s=1',
                                                                                                    'Museum of Fine Arts of Lyon',
                                                                                                    'The museum of Fine Arts of Lyon is the largest French museum outside of Paris, in terms of the richness of its collections, and the importance of the bequests received at its creation, in the wake of the Revolution. The ongoing enrichment of the collection and its dynamic artistic program place the museum on the international art scene. The museum is located place des Terreaux, in the heart of the city center, in a former 17th century Benedictine abbey. Its cloister is now home to a quiet garden featuring sculptures by Rodin and Bourdelle, among others. The museum’s encyclopedic collections are exhibited in five sections and seventy galleries presenting artworks from all the major civilizations and artistic schools, from Antiquity to the present day. All year long, the museum offers its visitors an exceptional tour of 5 000 years of art history and organizes major temporary exhibitions, as well as cultural activities engaging all audiences.',
                                                                                                    4.5,
                                                                                                    4.56,
                                                                                                    'Lyon',
                                                                                                    'https://www.mba-lyon.fr/fr'
                                                                                                   );

INSERT INTO museum(id, picture_link, name, description, rating, price, city_name, site_link) VALUES(20, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/08/29/56/5d/musee-des-confluences.jpg?w=1200&h=-1&s=1',
                                                                                                    'Musée des Confluences',
                                                                                                    'Unmissable in Lyon, the musée des Confluences tells the story of mankind and the history of life. Unprecedented in the world of European museums, it sets up a dialogue between all the sciences to better understand the world. Live out a unique experience by leafing through the great tale of the origins of humanity and life. At the heart of remarkable scenographies, let yourselves be guided through time and continents and be surprised by the beauty of the collections: meteorites, Camarasaurus, humming birds, femina sapiens, microscopes, Siberian tiger, sputnik, Samurai armours… Between Rhône and Saône, come to discover an audacious architecture and an exceptional site on the cutting edge of the Lyon peninsula.',
                                                                                                    4,
                                                                                                    3.50,
                                                                                                    'Lyon',
                                                                                                    'https://www.museedesconfluences.fr/'
                                                                                                   );

INSERT INTO hotel(id, name, hotel_class, picture, description, price, rating, city_name, site_address) VALUES (1, 'Le Tsuba Hotel', 4, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/0f/3a/f9/01/le-tsuba-hotel.jpg?w=1200&h=-1&s=1', 'Everyone needs a place to lay their weary head. For travelers visiting Paris, Le Tsuba Hotel is an excellent choice for rest and rejuvenation. Well-known for its romantic environment and proximity to great restaurants and attractions, Le Tsuba Hotel makes it easy to enjoy the best of Paris. Guest rooms offer amenities such as a flat screen TV, air conditioning, and a minibar, and guests can go online with free wifi offered by the hotel. Le Tsuba Hotel features a concierge and room service, to help make your stay more enjoyable. The property also boasts a wellness area with Sauna, Hammam and gym, and breakfast. Nearby landmarks such as Eiffel Tower (1.3 mi) and Palais Garnier - Opéra National de Paris (1.8 mi) make Le Tsuba Hotel a great place to stay when visiting Paris. Travelers looking to enjoy some shrimp can head to L''Avenue, L''Avant Comptoir, or Pedra Alta. Otherwise, you may want to check out a steakhouse such as Le Relais de l''Entrecote, La Poule au Pot, or Sacrée fleur. If you are interested in exploring Paris, check out one of the ancient ruins, such as Archeological Crypt of the Parvis of Notre-Dame, Arenes de Lutece, and Les vestiges des Tuileries. Whether you’re traveling for business, pleasure or both, Le Tsuba Hotel is sure to make your visit to Paris one worth remembering.', 777, 4.5, 'Paris', 'https://www.tripadvisor.com/Hotel_Review-g187147-d10521834-Reviews-Le_Tsuba_Hotel-Paris_Ile_de_France.html');
INSERT INTO hotel(id, name, hotel_class, picture, description, price, rating, city_name, site_address) VALUES (2, 'Hotel la Nouvelle Republique', 3, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/1d/4a/e7/60/chambre-triple.jpg?w=1200&h=-1&s=1', 'Hotel La Nouvelle République is a boutique hôtel located in the 11th district. The hotel La Nouvelle République welcomes you just few minutes away from metro lines 2, 3 and 11 as well as bus 96 which will take you directly to Rive Gauche. The property is at 5 minutes bike ride from Place de la République, if you just walk down Rue Oberkampf you can reach the Marais within 15 minutes. Decorated in a vintage quirky style, the soundproofed and air-conditioned rooms gives you the opportunity to relax fully. The hotel features mostly king size beds with all confort such as hairdryer, thick mattress, usb plugs, safe, tablet in rooms, tv screen. The hotel La Nouvelle République invites you every morning for a Free buffet breakfast. In addition, free drinks (tea, coffee, bottled water) are available throughout the day in our lobby. Finally, you can enjoy the excitement of this area where there are many exiting bars and restaurants. The 11th arrondissement is one of the most the creative scene of Paris. C''est le vrai Paris!'
                                                                                                              , 826, 5.0, 'Paris', 'https://www.tripadvisor.com/Hotel_Review-g187147-d12829774-Reviews-Hotel_la_Nouvelle_Republique-Paris_Ile_de_France.html');
INSERT INTO hotel(id, name, hotel_class, picture, description, price, rating, city_name, site_address) VALUES (3, 'InterContinental Paris - Le Grand', 4, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/1d/28/bd/f7/view-from-property.jpg?w=1200&h=-1&s=1', 'The InterContinental Paris Le Grand, opened during the reign of Napoleon III, is located in the very heart of the city: across the street from the Opéra Garnier, close to some of the best-known Parisian attractions, world-famous department stores and the wonderful Place Vendôme. The hotel contains 470 rooms, all richly decorated in the colours of the Opéra, and a warmly illuminated haven of peace and serenity in the bustle of Parisian life: la Verrière.
', 1.568, 4.5, 'Paris', 'https://www.tripadvisor.com/Hotel_Review-g187147-d207742-Reviews-InterContinental_Paris_Le_Grand-Paris_Ile_de_France.html');
INSERT INTO hotel(id, name, hotel_class, picture, description, price, rating, city_name, site_address) VALUES (4, 'Hotel La Manufacture', 3, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/0a/f2/0a/f3/chambre-eiffel.jpg?w=900&h=-1&s=1', 'Housed in a charming 19th century building, La Manufacture is an elegant establishment, decorated with taste and style and blending the past with the present. The use of soft, natural colors and the careful attention to detail reflects perfectly the inviting atmosphere to be found. Oak floorboards, wicker chairs, cotton rugs, wooden furniture make this charming Boutique Hotel a real feast for the eyes.'
                                                                                                              , 469, 4.5, 'Paris', 'https://www.tripadvisor.com/Hotel_Review-g187147-d197563-Reviews-Hotel_La_Manufacture-Paris_Ile_de_France.html');
INSERT INTO hotel(id, name, hotel_class, picture, description, price, rating, city_name, site_address) VALUES (5, 'Grand Hotel du Palais Royal', 5, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/1b/0f/c2/71/junior-suite.jpg?w=1200&h=-1&s=1', 'Ideally located between the Louvre Museum and the Opera house, opposite the Palais Royal gardens, this 5 star hotel, a refined cocoon beautifully decorated by the renowned Pierre-Yves Rochon, is a must-visit address in the heart of Paris. Behind its majestic facade listed as a historical monument, the hotel counts 68 rooms and suites, some with spectacular views of Paris, a restaurant, a bar (after work, tea time), a meeting room, a fitness center with Turkish bath and a Spa with a double cabin.'
                                                                                                              , 1.829, 5.0, 'Paris', 'https://www.tripadvisor.com/Hotel_Review-g187147-d617625-Reviews-Grand_Hotel_du_Palais_Royal-Paris_Ile_de_France.html');
INSERT INTO hotel(id, name, hotel_class, picture, description, price, rating, city_name, site_address) VALUES (6, 'Hotel Carlton Lyon - MGallery Collection', 4, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/0f/e1/a7/08/view-from-our-room.jpg?w=1200&h=-1&s=1', 'Be enchanted by the Carlton Lyon hotel, located on the peninsula. Despite being fully renovated in 2013, it has retained its authenticity and personality, blending contemporary comfort with sophistication. Relax in one of the 80 tastefully decorated rooms or in the cozy atmosphere of the bar. The hotel is just a 5-minute walk from the Bellecour metro stop and is the ideal base for exploring Lyon, with its opera house, museums, the St. Jean cathedral and more. Located between the Rhone and Saone rivers, the Carlton Lyon hotel was built in 1894 as a "petit palace" in Lyon. Punctuated by its forged iron balconies, dome and rotundas, its splendid Haussmann facade echoes the refinement of its historical decor. The hotel''s "Signature" rooms also highlight artists who have stayed there. Travelers will appreciate immersing themselves in the splendid adornments of the many talents that have visited the hotel.'
                                                                                                              , 697, 4.5, 'Lyon', 'https://www.tripadvisor.com/Hotel_Review-g187265-d232355-Reviews-Hotel_Carlton_Lyon_MGallery_Collection-Lyon_Rhone_Auvergne_Rhone_Alpes.html');
INSERT INTO hotel(id, name, hotel_class, picture, description, price, rating, city_name, site_address) VALUES (7, 'Hotelo, l''hotel au coeur de Lyon', 2, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/0a/6d/c4/e4/imag4061-largejpg.jpg?w=800&h=-1&s=1', 'The Hotelo hotel is ideally situated (close to Perrache and Ampère metro stations) in the very heart of Lyon, a designated UNESCO World Heritage site. The hotel is only a stone''s throw from Perrache train station, with both Place Carnot and Place Bellecour on the doorstep. The districts of Gerland and La Confluence are easily accessible, as are the Jean Moulin, Lyon 2 and Catholic Universities. The Hotelo hotel is an ideal base for you to visit the town and surrounding areas, from Vieux Lyon to the more modern quarter of La Confluence. Plus it''s easy to get around without your car with the metro, bus, tramway and Vélo''V bicycle hire all within easy reach. A perfect way to visit our beautiful city of Lyon! The hotel is entirely non-smoking. All rooms are en-suite with either a shower or a bath and are equipped with flat-screen TV, telephone and free Wifi access. Our friendly team is on hand to personally welcome you to the Hotelo Hotel.'
                                                                                                              , 398, 4.5, 'Lyon', 'https://www.tripadvisor.com/Hotel_Review-g187265-d1377912-Reviews-Hotelo_l_hotel_au_coeur_de_Lyon-Lyon_Rhone_Auvergne_Rhone_Alpes.html');
INSERT INTO hotel(id, name, hotel_class, picture, description, price, rating, city_name, site_address) VALUES (8, 'Boscolo Lyon Hotel & Spa', 5, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/18/59/a1/54/fredericdurantet.jpg?w=1200&h=-1&s=1', 'In a splendid setting between the Rhone and the Saone, in the most prestigious district of Lyon, a few steps from Place Bellecour and the Grand Hotel-Dieu, a majestic 19th century building, expertly renovated, is intended to become one of the most luxurious 5 stars hotel of Lyon. Let yourself be enchanted by the view from your room overlooking the Rhone or relax, while tasting a “ristretto coffee” in the luxurious Grand Salon, created in the former winter garden surmounted by a panoramic glass roof. The peculiarity of the Luxury Hotel Boscolo Lyon consists in the delicate balance between the splendor of the Haussmann’ architecture and the loveliness of the atmospheres created by the black marble, Vicenza stone, sophisticated curtains and other precious materials. Once acquired the hotel in 2002, Angelo Boscolo undertakes its metamorphosis at the end of 2018. The doors of the new Boscolo Lyon open.'
                                                                                                              , 1.051, 4.5, 'Lyon', 'https://www.tripadvisor.com/Hotel_Review-g187265-d15111966-Reviews-Boscolo_Lyon_Hotel_Spa-Lyon_Rhone_Auvergne_Rhone_Alpes.html');
INSERT INTO hotel(id, name, hotel_class, picture, description, price, rating, city_name, site_address) VALUES (9, 'H10 Madison', 4.5, 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/12/41/96/56/h10-madison.jpg?w=1200&h=-1&s=1',
                                                                                                               'Located in the heart of Barcelona, just 240 metres from the cathedral, the H10 Madison is a new four-star superior hotel. The hotel features elegant rooms, a full range of dining options with restaurant and Lobby Bar, two meeting rooms and an amazing terrace with plunge pool and close-up views of the cathedral.'
